mapper.select:
SELECT COUNT(accommodation_no) FROM accommodation
WHERE category = #{category}

        AND region = #{region}


        AND start_date >= #{start_date}


        AND end_date <= #{end_date}

SELECT * FROM accommodation
WHERE category = #{category}

        AND region = #{region}


        AND start_date >= #{start_date}


        AND end_date <= #{end_date}

ORDER BY

        star_rating DESC
        review_count DESC
        star_rating DESC

LIMIT #{start}, #{amount}
